import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Button, Checkbox, Form } from 'semantic-ui-react';

const preferences = [
	'',
	'',

]

class SignupForm extends Component {
	constructor(props) {
		super(props);
		this.state = {
			username: '',
			password: '',
			first_name: '',
			last_name: '',
			email: '',
			contact: '',
			birth: new Date(),
			gender: '',
			preferece_one: '',
			preferece_two: '',
		};
	}


	handle_change = e => {
		const name = e.target.name;
		const value = e.target.value;
		this.setState(prevstate => {
			const newState = { ...prevstate };
			newState[name] = value;
			return newState;
		});
	};

	renderPreferences = () => {
		preferences = preferences.map((item) => {
			<Form.Field>
				<Radio
					label={item}
					name='preference_one'
					value={this.state.preferece_one}
					checked={this.state.preferece_one === item}
					onChange={this.handle_change}
				/>
			</Form.Field>
		})
	}

	render() {
		return (
			<div>

				<Form>
					<Form.Field>
						<label htmlFor="username">Username</label>
						<input
							type="text"
							name="username"
							value={this.state.username}
							onChange={this.handle_change}
						/>
					</Form.Field>
					<Form.Field>
						<label htmlFor="password">Password</label>
						<input
							type="password"
							name="password"
							value={this.state.password}
							onChange={this.handle_change}
						/>
					</Form.Field>
					<Form.Field>
						<label htmlFor="email">Email</label>
						<input
							type="email"
							name="email"
							value={this.state.email}
							onChange={this.handle_change}
						/>
					</Form.Field>
					<Form.Field>
						<label htmlFor="first_name">First Name</label>
						<input
							type="text"
							name="first_name"
							value={this.state.first_name}
							onChange={this.handle_change}
						/>
					</Form.Field>
					<Form.Field>
						<label htmlFor="last_name">Last Name</label>
						<input
							type="text"
							name="last_name"
							value={this.state.last_name}
							onChange={this.handle_change}
						/>
					</Form.Field>
					<Form.Field>
						<label htmlFor="contact">Last Name</label>
						<input
							type="text"
							name="contact"
							value={this.state.contact}
							placeholder="핸드폰번호 11자리를 입력하세요."
							onChange={this.handle_change}
						/>
					</Form.Field>
					<Form.Field>
						취향을 선택하세요:
					</Form.Field>
					<Form.Field>
						<Radio
							label='설레는'
							name='radioGroup'
							value='this'
							checked={this.state.value === 'this'}
							onChange={this.handleChange}
						/>
					</Form.Field>

				</Form>
			</div>

		);
	}
}

export default SignupForm;

SignupForm.propTypes = {
	handle_signup: PropTypes.func.isRequired
};
